customCSS:
#header-bar {
    box-shadow: none !important;
} 

.postInfo {
    background: none !important;
    border: none !important;
    box-shadow: none !important;
}

.op {
    margin: 10px 0 20px !important;
}

.postContainer,
.summary {
    display: block !important;
    margin: 0 auto !important;
    width: 50% !important;
} 

@media only screen and (max-width:960px) {
    .postContainer,
    .summary {
        width: 75% !important;
    }

}

hr {
    display: none !important;
}

.inline, .postMessage, .reply {
    background: #f0eae6 !important;
    color: #3e5059 !important;
}

.header-gradient:root #header-bar {
    background: none !important;
}

body, 
input {
    font-size: 14px !important;
    font-family: Roboto !importanrt;
}
.warning {
    color: #99736e !important;
}

.post {
    background-color: #f0eae6 !important;
    padding: 20px 20px !important;
}

.hide-reply-button, 
.name {
    display: none !important;
}

.reply-hide:root .thread > .expanded-image > .reply, .reply-hide:root .thread > .hasInline > .reply {
    margin-left: 0 !important;
}

select,  input, label, input.field, ::-moz-placeholder  {
    color: #3e5059 !important;
}

textarea, textarea:hover {
    background: #3e5059 !important;
}
